<?phpclass Database{  private $DB_HOST;  private $DB_USER;  private $DB_PASS;  private $DB_NAME;  private $conn;  public function __construct($db_host, $db_user, $db_pass){    $this->DB_HOST = $db_host;    $this->DB_USER = $db_user;    $this->DB_PASS = $db_pass;  }    public function connect_db(){    $this->conn = mysql_connect($this->DB_HOST, $this->DB_USER, $this->DB_PASS);      }    public function choose_database_name($database_name){    $this->DB_NAME = $database_name;    mysql_select_db($database_name,$this->conn);    return $this->DB_NAME;  }    public function escape($variable){    return mysql_real_escape_string($variable);  }    public function execute($query){ //execute query only    $this->_queryHelper($query);  }    public function query($query){ //execute query and returning data    $result = $this->_queryHelper($query);    $data = array();    while ($row = mysql_fetch_array($result, MYSQL_ASSOC)){      $data[] = (object)$row;    }    return $data;  }    private function _queryHelper($query) {	return mysql_query($query,$this->conn);  }    public function close_db(){    mysql_close($this->conn);  }}//fbchat Table/*CREATE TABLE fbuser(  fb_uid VARCHAR(25) NOT NULL PRIMARY KEY,  session TEXT ) TYPE = innodb;CREATE TABLE bbuser(  bb_pin VARCHAR(10) NOT NULL PRIMARY KEY,  fb_uid VARCHAR(25) NOT NULL,  last_seen INT(12) ) TYPE = innodb;CREATE INDEX FB_UID on bbuser(fb_uid);//*/